mapper.select:
SELECT c.num, c.content, c.good, c.cheer, c.member_num, m.name as memberName, c.mentoring_post_num, c.review_post_num, c.fashion_post_num FROM COMMENT c JOIN MEMBER m ON c.member_num = m.num WHERE
c.fashion_post_num = #{postNum}
c.review_post_num = #{postNum}
c.mentoring_post_num = #{postNum}
ORDER BY c.num ASC

mapper.update:
UPDATE COMMENT
SET content = #{content}
WHERE num = #{num}
